    private void Convert8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Convert8ActionPerformed
        double weight;
        double height;
        double bMI=0;
        String a;

        weight =Double.parseDouble(Text15.getText());
        height =Double.parseDouble(Text16.getText());
        bMI = (weight * 2.2046 * 703) / (height * height*144);
        Text25.setText(String.valueOf(bMI));

        if (bMI<18.5){
            a="You are Underweight. (less than 18.5 is Underweight)";
        }
        else if(bMI>18.4 && bMI < 25){
            a="Your BMI is Normal. (between 18.5 and 24.9 is normal)";
        }
        else if (bMI>24.9 && bMI<30){
            a="You are Overweight. (between 25 and 29.9 is Overweight)";
        }
        else if (bMI>29.9){
            a="You are Obese. (30 or greater is Obese)";
        }
        else {
            a="Input incorrect";
        }
        Text26.setText(String.valueOf(a));
    }//GEN-LAST:event_Convert8ActionPerformed

